dictionaries.styles:
span[title] {
    cursor: help;
}

ul[data-sc-content="sense-groups"] {
    list-style-type: "＊";
}

li[data-sc-content="sense-group"],
li[data-sc-content="forms"] {
    padding-left: 0.25em;
}

div[data-sc-content="sense-group"],
li[data-sc-content="sense-group"]:first-child {
    margin-top: 0.1em;
}

li[data-sc-content="sense-group"] + li[data-sc-content="sense-group"],
div[data-sc-content="forms"],
li[data-sc-content="forms"] {
    margin-top: 0.5em;
}

li[data-sc-content="sense"] {
    padding-left: 0.25em;
    & ul[data-sc-content="glossary"] {
        list-style-type: none;
        padding-left: 0.25em;
    }
}

span[data-sc-class="tag"] {
    border-radius: 0.3em;
    font-size: 0.8em;
    font-weight: bold;
    margin-right: 0.5em;
    padding: 0.2em 0.3em;
    vertical-align: text-bottom;
    word-break: keep-all;
}

span[data-sc-content="part-of-speech-info"] {
    background-color: #565656;
    color: white;
}

span[data-sc-content="misc-info"] {
    background-color: brown;
    color: white;
}

span[data-sc-content="field-info"] {
    background-color: purple;
    color: white;
}

span[data-sc-content="dialect-info"] {
    background-color: green;
    color: white;
}

span[data-sc-content="lang-source-wasei"] {
    background-color: orange;
    color: black;
    margin-left: 0.5em;
    margin-right: 0;
}

span[data-sc-content="forms-label"] {
    background-color: #565656;
    color: white;
}

ul[data-sc-content="glossary"] {
    list-style-type: disc;
}

span[data-sc-content="registered-trademark"] {
    vertical-align: super;
    font-size: 0.6em;
}

div[data-sc-class="extra-box"] {
    border-radius: 0.4rem;
    border-style: none none none solid;
    border-width: calc(3em / var(--font-size-no-units, 14));
    margin-bottom: 0.5rem;
    margin-top: 0.5rem;
    padding: 0.5rem;
    width: fit-content;
}

div[data-sc-class="extra-content"] {
    margin-left: 0.5rem;
}

div[data-sc-content="info-gloss"] {
    border-color: green;
    background-color: color-mix(in srgb, green 5%, transparent);
}

div[data-sc-content="sense-note"] {
    border-color: goldenrod;
    background-color: color-mix(in srgb, goldenrod 5%, transparent);
}

div[data-sc-content="lang-source"] {
    border-color: purple;
    background-color: color-mix(in srgb, purple 5%, transparent);
}

div[data-sc-content="xref"] {
    border-color: #1A73E8;
    background-color: color-mix(in srgb, #1A73E8 5%, transparent);
    & span[data-sc-content="reference-label"] {
        color: #1A73E8;
    }
}

div[data-sc-content="antonym"] {
    border-color: brown;
    background-color: color-mix(in srgb, brown 5%, transparent);
    & span[data-sc-content="reference-label"] {
        color: brown;
    }
}

div[data-sc-content="example-sentence"] {
    border-color: var(--text-color, var(--fg, #333));
    background-color: color-mix(in srgb, var(--text-color, var(--fg, #333)) 5%, transparent);
}

div[data-sc-content="extra-info"] {
    margin-left: 0.5em;
}

div[data-sc-class="extra-label"] {
    color: #777;
    font-size: 0.8em;
    font-style: italic;
}

span[data-sc-content="reference-label"] {
    font-size: 0.8em;
    margin-right: 0.5rem;
}

div[data-sc-content="xref-content"],
div[data-sc-content="antonym-content"] {
    font-size: 1.3em;
}

div[data-sc-content="xref-glossary"],
div[data-sc-content="antonym-glossary"] {
    font-size: 0.8rem;
}

div[data-sc-content="example-sentence-a"] {
    font-size: 1.3em;
}

div[data-sc-content="example-sentence-b"] {
    font-size: 0.8em;
}

span[data-sc-content="example-keyword"] {
    color: color-mix(in srgb, lime, var(--text-color, var(--fg, #333)));
}

span[data-sc-content="attribution-footnote"] {
    color: #777;
    font-size: 0.8em;
    margin-left: 0.25rem;
    vertical-align: top;
}

div[data-sc-content="graphic"] {
    margin-bottom: 0.5rem;
    margin-top: 0.5rem;
    & .gloss-image-container {
        border: solid calc(42em / var(--font-size-no-units, 14)) var(--text-color, var(--fg, #333));
        border-radius: 3%;
    }
}

div[data-sc-content="graphic-attribution"] {
    font-size: 70%;
    margin-top: 0.25rem;
}

div[data-sc-content="forms"],
li[data-sc-content="forms"] {
    & ul {
        font-size: 1.2em;
        list-style-type: circle;
    }
    & table {
        margin-top: 0.2em;
    }
    & tr {
        border-width: 1px;
    }
    & th {
        border-width: 1px;
        font-weight: normal;
    }
    & td {
        border-width: 1px;
        text-align: center;
        & span {
            clip-path: circle();
            display: block;
            font-weight: bold;
            padding: 0 0.5em;
        }
    }
}

tr[data-sc-content="forms-col-senses-row"] th {
    text-align: center;
}

tr[data-sc-content="forms-header-row"] th {
    font-size: 1.2em;
    text-align: center;
    vertical-align: text-bottom;
    & span[data-sc-class="old-character"] {
        text-emphasis-style: dot;
    }
}

th[data-sc-content="forms-row-senses"] {
    text-align: center;
}

span[data-sc-class="form-special"] {
    color: crimson;
}

td[data-sc-class="form-pri"] > span {
    color: white;
    background: radial-gradient(green 50%, white 100%);
    &::before {
        content: "△";
    }
}

td[data-sc-class="form-irr"] > span {
    color: white;
    background: radial-gradient(crimson 50%, white 100%);
    &::before {
        content: "✕";
    }
}

td[data-sc-class="form-out"] > span {
    color: white;
    background: radial-gradient(blue 50%, white 100%);
    &::before {
        content: "古";
    }
}

td[data-sc-class="form-old"] > span {
    color: white;
    background: radial-gradient(blue 50%, white 100%);
    &::before {
        content: "旧";
    }
}

td[data-sc-class="form-rare"] > span {
    color: white;
    background: radial-gradient(purple 50%, white 100%);
    &::before {
        content: "▽";
    }
}

td[data-sc-class="form-valid"] > span {
    color: var(--background-color, var(--canvas, #f8f9fa));
    background: radial-gradient(var(--text-color, var(--fg, #333)) 50%, white 100%);
    &::before {
        content: "◇";
    }
}

div[data-sc-content="attribution"] {
    font-size: 0.7em;
    text-align: right;
}

div[data-sc-content="redirect-glossary"] {
    font-size: 180%;
    margin-top: 0.2em;
}

body {
    margin: 0;
}

div[data-sc-header] {
    color: var(--daijisen-header-text, var(--background-color, var(--background-color-light, #FFFFFF)));
}

div[data-sc-header][data-sc-d-j-s] {
	background-color: var(--daijisen-header-djs, var(--tag-archaism-background-color, #B65058));
}

div[data-sc-header][data-sc-d-j-s-p] {
	background-color: var(--daijisen-header-djs, var(--tag-archaism-background-color, var(--disabled-color-lighter, var(--background-color, #999999))));
}

div[data-sc-headertitle],
div[data-sc-header-title] {
	margin-block-start: 0.05em;
	margin-block-end: 0.05em;
	margin-inline-start: 8px;
	margin-inline-end: 8px;
	font-size: 0.7em;
	line-height: 1.333em;
}

div[data-sc-contents] {
	font-size: 1.5em;
/* 	line-height: 1.5em;
	margin-block-end: 1em;
	margin-inline-start: 0.5em;
	margin-inline-end: 0.5em; */
}
/* 
div[data-sc見出-g] {
	margin-block-end: 0.5em;
}

div[data-sc-subitem] {
	margin-block-start: 0.8em;
}

div[data-sc-subitem-c] {
	margin-inline-start: 0.5em;
} */

span[data-sc-headword][data-sc見出] {
	font-weight: bold;
}

span[data-sc-headword][data-sc表記],
span[data-sc-headword][data-sc原綴] {
    margin-inline-start: 0.25em;
}

span[data-sc-headword][data-sc歴史] {
	margin-inline-start: 0.417em;
	font-size: 0.7em;
}

span[data-sc-hhyphen] {
	font-weight: bold;
	font-size: 1.2em;
	vertical-align: 0.03em;
	margin-inline-start: 0.03em;
	margin-inline-end: 0.03em;
}

span[data-sc-hdot] {
	font-size: 0.8em;
}

span[data-sc-wdot] {
	font-size: 0.45em;
}

span[data-sc-hyoki-s] {
	vertical-align: -0.1em;
}

div[data-sc-m-g][data-sc-l1-a] {
	margin-block-start: 0.5em;
}

div[data-sc-m-g][data-sc-l2-a],
div[data-sc-m-g][data-sc-l3-a] {
	margin-block-start: 0.3em;
}

div[data-sc-meaning][data-sc-c] {
	margin-block-start: 0.3em;
	margin-block-end: 0.3em;
}

div[data-sc-campaigndesc],
div[data-sc-campaign-desc] {
	margin-block-start: 0.3em;
	font-size: 0.9em;
}

div[data-sc-campaigndesc][data-sc-inline] {
	display: inline;
	margin-block-start: 0em;
}

div[data-sc-campaignex],
div[data-sc-campaign-ex] {
	font-size: 0.9em;
	text-indent: -0.9em;
	margin-inline-start: 0.9em;
}

div[data-sc-campaignname],
div[data-sc-campaign-name] {
	margin-inline-start: 1.05em;
	font-size: 0.8em;
}

span[data-sc-ex-g][data-sc-b-m] {
	margin-inline-start: 0.4em;
}

span[data-sc-ex-s] {
	margin-inline-start: 0.2em;
	font-size: 0.9em;
}

span[data-sc-l4] {
	font-weight: bold;
}

span[data-sc-l4][data-sc-small] {
	font-size: 0.9em;
}

table[data-sc-mean-t] {
	display: table;
	font-size: 0.8em;
	line-height: 1.5em;
	margin-block-start: 0.5em;
	margin-block-end: 0.5em;
	border-block-start: solid 0.05em gray;
	border-block-end: none;
	border-inline-start: solid 0.05em gray;
	border-inline-end: none;
}

td[data-sc-mean-t-d] {
	display: table-cell;
	padding: 0.3em;
	border-block-start: none;
	border-block-end: solid 0.05em gray;
	border-inline-end: solid 0.05em gray;
	border-inline-start: none;
}

div[data-sc-m-image-g] {
	margin-block-start: 0.3em;
	margin-block-end: 0.3em;
}

div[data-sc-m-image] {
	margin: 0.5em;
}

div[data-sc-m-caption] {
	font-size: 0.75em;
	line-height: 1.5em;
}

div[data-sc-m-url-g],
div[data-sc-m-location-g] {
	margin-block-start: 0.5em;
	margin-block-end: 0.5em;
}

div[data-sc-m-url],
div[data-sc-m-location] {
	font-size: 0.9em;
	line-height: 1.5em;
}

span[data-sc-m-url][data-sc-t-m] {
	margin-block-start: 0.3em;
}

div[data-sc-m-urlurl],
div[data-sc-m-url-url] {
	font-size: 0.9em;
}

/* div[data-sc-m-accent-audio-g],
div[data-sc-m-accentaudio-g] {
	margin-block-start: 0.3em;
	margin-block-end: 0.3em;
} */

span[data-sc-m-accent-m] {
	color: var(--daijisen-accent-mark, var(--danger-color, var(--sidebar-button-danger-background-color-hover, #CC0000)));
}

span[data-sc-m-accent-slash],
span[data-sc-m-accentslash] {
	font-size: 0.9em;
	color: var(--daijisen-accent-slash, var(--text-color-light4, #808080));
/* 	margin-inline-start: 0.2em;
	margin-inline-end: 0.2em; */
}

img[data-sc-audio] {
	display: none;
}

span[data-sc-rect] {
	padding-block-start: 0.04em;
	padding-block-end: 0.58em;
	padding-inline: 0.05em;
	font-size: 0.8em;
	border-width: 0.05em;
	border-style: solid;
	border-color: var(--text-color);
	word-break: keep-all;
	-webkit-border-radius: 0.1em;
}

span[data-sc-rect][data-sc-fill] {
	padding: 0.1em;
	color: var(--background-color-light, var(--background-color));
	border-style: none;
	background-color: var(--text-color);
}

span[data-sc-rect][data-sc-l3] {
	padding: 0.1em 0.15em 0.1em 0.19em;
	padding-block: 0.1em;
	padding-inline-start: 0.19em;
	padding-inline-end: 0.15em;
	color: var(--background-color-light, var(--background-color));
	border-style: none;
	background-color: var(--daijisen-background-l3, var(--text-color-light2, #606060));
}

span[data-sc-rect][data-sc-small][data-sc-l3] {
	vertical-align: 0.1em;
}

span[data-sc-rect][data-sc-bold] {
	font-weight: bold;
}

span[data-sc-rect][data-sc-small] {
	font-size: 0.72em;
}

span[data-sc-circle] {
	padding-block-start: 0.06em;
	padding-block-end: 0.08em;
	padding-inline: 0.1em;
	font-size: 0.8em;
	border-width: 0.05em;
	border-style: solid;
	border-color: var(--text-color);
	-webkit-border-radius: 0.67em;
}

span[data-sc補足ロゴ-g] {
	font-size: 0.8em;
	background-color: var(--daijisen-logo-color, var(--text-color, #000000));
	-webkit-border-radius: 0.2em;
	padding: 0.09em 0.25em 0.11em 0.25em;
	word-break: keep-all;
	vertical-align: 0.05em;
}

span[data-sc補足ロゴ] {
	background-color: var(--background-color-light, var(--background-color));
	color: var(--text-color);
	border-block-start: solid 0.05em var(--daijisen-logo-color, var(--text-color, #000000));
	border-block-end: solid 0.05em var(--daijisen-logo-color, var(--text-color, #000000));
	border-inline-end: none;
	border-inline-start: none;
	padding-block-start: 0.04em;
	padding-block-end: 0.06em;
	padding-inline: 0.08em;
}

span[data-sc補足ロゴ-g][data-sc-small] {
	font-size: 0.7em;
}

span[data-sc-hinshi-sahen] {
	font-size: 0.5em;
	vertical-align: 1.1em;
	margin-inline-end: 0.6em;
}

span[data-sc季語ロゴ前] {
	background-color: var(--background-color-light, var(--background-color));
	color: var(--text-color);
	border-block-start: solid 0.05em var(--text-color);
	border-block-end: solid 0.05em var(--text-color);
	border-inline-end: solid 0.05em var(--background-color-light, var(--background-color));
	border-inline-start: none;
	padding-block-start: 0.04em;
	padding-block-end: 0.06em;
	padding-inline: 0;
}

span[data-sc季語ロゴ後] {
	background-color: var(--background-color-light, var(--background-color));
	color: var(--text-color);
	border-block-start: solid 0.05em var(--text-color);
	border-inline-end: none;
	border-block-end: solid 0.05em var(--text-color);
	border-inline-start: solid 0.05em var(--background-color-light, var(--background-color));
	padding-block-start: 0.04em;
	padding-block-end: 0.06em;
	padding-inline: 0;
}

span[data-sc季語ロゴ中] {
	background-color: var(--background-color-light, var(--background-color));
	color: var(--text-color);
	padding-block-start: 0.1em;
	padding-block-end: 0.12em;
	padding-inline: 0;
}

span[data-sc季語ロゴ中] {
	background-color: var(--background-color-light, var(--background-color));
	color: var(--text-color);
	padding-block-start: 0.1em;
	padding-block-end: 0.12em;
	padding-inline: 0;
}

span[data-sc季語ロゴ-s] {
	font-size: 0.4em;
}

span[data-sc季語-m] {
	font-weight: bold;
	font-size: 0.85em;
	margin-inline-end: 0.3em;
}

span[data-sc季語] {
	font-size: 1.1em;
}

span[data-sc-lang] {
	font-size: 0.7em;
}

span[data-sc-lang][data-sc-f-m] {
	margin-inline-end: 0.3em;
}

span[data-sc-lang][data-sc-b-m] {
	margin-inline-start: 0.3em;
}

span[data-sc-lang][data-sc-i-m] {
	margin-inline-start: 0.3em;
	margin-inline-end: 0.3em;
}

span[data-sc歴史] {
	font-size: 0.7em;
}

span[data-sc-bold] {
	font-weight: bold;
}

span[data-sc-italic] {
	font-style: italic;
}

span[data-sc-sub] {
	font-size: 0.6em;
	font-weight: normal;
	vertical-align: -0.7em;
}

span[data-sc-sub][data-sc-capital] {
	font-size: 0.5em;
	vertical-align: -1.1em;
}

span[data-sc-sub][data-sc-kana] {
	vertical-align: -0.7em;
}

span[data-sc-sub][data-sc-d-j-s-p_kana] {
	font-size: 0.7em;
	vertical-align: -0.45em;
}

span[data-sc-sup] {
	font-size: 0.6em;
	vertical-align: 1.1em;
}

span[data-sc-sup][data-sc-kaeriten] {
	font-size: 0.5em;
	vertical-align: -1em;
}

span[data-sc常外音訓-m] {
	font-size: 0.38em;
	vertical-align: 1.9em;
}

span[data-sc常外-m] {
	font-size: 0.55em;
	vertical-align: 1em;
}

span[data-sc付表有-m],
span[data-sc付表無-m] {
	font-size: 0.45em;
}

span[data-sc-white-diamond],
span[data-sc-black-diamond],
span[data-sc-whitediamond],
span[data-sc-blackdiamond] {
	font-size: 0.7em;
	margin-inline-end: 0.3em;
}

span[data-sc-k-v] {
	display: none;
}

span[data-sc-wari],
span[data-sc-small] {
	font-size: 0.7em;
	vertical-align: -0.4em;
}

span[data-sc-rect][data-sc-small] {
	vertical-align: 0;
}

span[data-sc-headword] span[data-sc-wari],
span[data-sc-bold] span[data-sc-wari] {
	font-weight: normal;
}

span[data-sc-gaiji][data-sc-dakuten],
span[data-sc-gaiji][data-sc-handakuten] {
	margin-inline-start: 0.3em;
	margin-inline-end: -0.5em;
}

img[data-sc-gaiji] {
	height: 1em;
}

a {
	text-decoration: none;
	color: var(--text-color);
}

a:link {
	color: var(--daijisen-blue, var(--accent-color, var(--link-color, #4a8ade)));
}

a[data-sc-blue] {
	color: var(--daijisen-blue, var(--accent-color, var(--link-color, #4a8ade)));
}

a[data-sc-blue] span[data-sc-rect],
a[data-sc-blue] span[data-sc-circle] {
	border-color: var(--daijisen-blue, var(--accent-color, var(--link-color, #4a8ade)));
}

a[data-sc-blue] span[data-sc-rect][data-sc-fill],
a[data-sc-blue] span[data-sc-rect][data-sc-l-3] {
	background-color: var(--daijisen-blue, var(--accent-color, var(--link-color, #4a8ade)));
}

span[data-sc-f-m] {
	margin-inline-end: 0.15em;
}

span[data-sc-b-m] {
	margin-inline-start: 0.15em;
}

span[data-sc-i-m] {
	margin-inline-start: 0.15em;
	margin-inline-end: 0.15em;
}

span[data-sc-img] .gloss-image-link {
    max-width: 70% !important;
}

.gloss-image-container {
	max-height: 100% !important;
}

span[data-sc-id="index"] {
    display: none;
}

div[data-sc-header][data-sc-d-j-s],
div[data-sc-header][data-sc-d-j-s-p] {
    display: none;
}

/* 
Optional: Remove or comment to show the 類語 and Pitch accent sections
		  (Hidden by default)
*/
div[data-sc-meaning][data-sc-c][data-sc-class="C"]:has(span[data-sc-href="$c-ruigo"]),
div[data-sc-meaning][data-sc-c][data-sc-class="C"]:has(span[data-sc-href="$c-ssgo"]) {
	display: none;
}

div[data-sc-m-accent-audio-g] {
	display: none;
}

a:hover {
    color: var(--text-color);
    filter: brightness(1.3);
    text-decoration: underline;
    text-decoration-color: oklch(65% 0.2 260);
}

.gloss-image-link {
    filter: none !important;
}

/* ---- 外字 ---- */
/* 関連　*/
[data-sc外字][data-sc左丸] {
	display: inline-block;
	font-family: "ヒラギノ丸ゴ ProN";
	border: 1px solid var(--rgko12-blue, var(--accent-color, var(--shadow-focus, #4a8ade)));
	border-radius-topleft: 8px;
	border-top-left-radius: 8px;
	border-bottom-left-radius: 8px;
	font-weight: bold;
	padding-block: -0.1em;
	padding-inline: 0.2em;
	vertical-align: baseline;
	text-align: center;
